export default function AnimatedLogoSVG({ className = "" }) {
  const wrapperRef = useRef(null);

  useEffect(() => {
    const wrapper = wrapperRef.current;
    if (!wrapper) {
      return;
    }

    const svg = wrapper.querySelector("svg");
    if (!svg) {
      return;
    }

    const viewBox = svg.viewBox?.baseVal;
    const centerX = viewBox ? viewBox.x + viewBox.width / 2 : 0;
    const centerY = viewBox ? viewBox.y + viewBox.height / 2 : 0;

    const allPaths = Array.from(svg.querySelectorAll("path")).filter(
      (path) => !path.closest("defs")
    );
    const strokePaths = [];
    const textPaths = [];
    const frontPaths = [];

    allPaths.forEach((path) => {
      const directFill = path.getAttribute("fill");
      const parentFill = path.closest("g[fill]")?.getAttribute("fill");
      const fillAttr = (directFill || parentFill || "").toLowerCase();
      const isText = fillAttr === "#242424";
      const isFront = fillAttr === "#4a3931";
      if (isText) {
        textPaths.push(path);
        return;
      }

      let length = 0;
      try {
        length = path.getTotalLength();
      } catch {
        length = 0;
      }

      let boxCenterX = centerX;
      let boxCenterY = centerY;
      try {
        const box = path.getBBox();
        boxCenterX = box.x + box.width / 2;
        boxCenterY = box.y + box.height / 2;
      } catch {
        boxCenterX = centerX;
        boxCenterY = centerY;
      }

      const distance = Math.hypot(boxCenterX - centerX, boxCenterY - centerY);
      if (isFront) {
        frontPaths.push(path);
      }

      strokePaths.push({ path, length: Math.max(length, 1), distance });
    });

    strokePaths.sort((a, b) => b.distance - a.distance);

    const stagger = 0.028;
    const baseDelay = 0.2;
    const totalStagger = strokePaths.length * stagger;
    const cycle = Math.max(14, totalStagger + 8);

    svg.style.setProperty("--cycle", `${cycle}s`);

    strokePaths.forEach(({ path, length }, index) => {
      path.classList.add("logo-path");
      path.style.setProperty("--path-length", length);
      path.style.setProperty("--draw-delay", `${baseDelay + index * stagger}s`);
    });

    frontPaths.forEach((path) => {
      path.classList.add("logo-fill");
    });

    textPaths.forEach((path) => {
      path.classList.add("logo-text");
    });
  }, []);

  return (
    <>
      <style>{`
        .animated-logo-svg {
          color: var(--ink);
          --cycle: 14s;
          --stroke-width: 2;
          display: inline-flex;
        }

        .animated-logo-svg svg {
          width: 100%;
          height: 100%;
          display: block;
        }

        .animated-logo-svg path {
          fill: none;
          stroke: currentColor;
          stroke-width: var(--stroke-width);
          stroke-linecap: round;
          stroke-linejoin: round;
          vector-effect: non-scaling-stroke;
        }

        .animated-logo-svg path.logo-path {
          opacity: 1;
          stroke-dasharray: var(--path-length);
          stroke-dashoffset: var(--path-length);
          animation: logoDraw var(--cycle) ease-in-out infinite;
          animation-delay: var(--draw-delay, 0s);
        }

        .animated-logo-svg path.logo-fill {
          fill: var(--accent);
          fill-opacity: 0;
          animation: logoDraw var(--cycle) ease-in-out infinite,
            fillPulse var(--cycle) ease-in-out infinite;
          animation-delay: var(--draw-delay, 0s), var(--draw-delay, 0s);
        }

        .animated-logo-svg path.logo-text {
          display: none;
        }

        @keyframes fillPulse {
          0% {
            fill-opacity: 1;
          }
          45% {
            fill-opacity: 0.55;
          }
          60% {
            fill-opacity: 0.22;
          }
          80% {
            fill-opacity: 0;
          }
          100% {
            fill-opacity: 0;
          }
        }

        @keyframes logoDraw {
          0% {
            stroke-dashoffset: var(--path-length);
          }
          45% {
            stroke-dashoffset: 0;
          }
          100% {
            stroke-dashoffset: var(--path-length);
          }
        }

        @media (prefers-reduced-motion: reduce) {
          .animated-logo-svg path.logo-path {
            animation: none;
            opacity: 1;
            stroke-dasharray: none;
            stroke-dashoffset: 0;
          }

          .animated-logo-svg path.logo-fill {
            animation: none;
            fill-opacity: 0.22;
          }
        }
      `}</style>
      <span
        ref={wrapperRef}
        className={`animated-logo-svg ${className}`}
        role="img"
        aria-label="Faako Logo"
        dangerouslySetInnerHTML={{ __html: LOGO_SVG }}
      />
    </>
  );
}
